function add (a, b) {
  // 位数对齐，位数不够的前面补0
  const maxLength = Math.max(a.length, b.length)
  a = a.padStart(maxLength, 0)
  b = b.padStart(maxLength, 0)

  let t = 0 // 当前位之和
  let f = 0 // 进位，0或者1两种取值
  let sum = ''
  for (let i = maxLength - 1; i >= 0; i--) {
    t = Number.parseInt(a[i]) + Number.parseInt(b[i]) + f
    f = Math.floor(t / 10)
    sum = (t % 10) + sum
  }

  if (f === 1) {
    sum = '1' + sum
  }

  return sum
}

